(function(){var t=function(){var t=document.querySelectorAll("video, object, iframe");t.forEach(function(t){if(t===undefined){return}var e=t.parentNode.offsetWidth||window.innerWidth*.95;if(!t.dataset.origwidth){t.dataset.origwidth=t.getAttribute("width");t.dataset.origheight=t.getAttribute("height")}var i=e/t.dataset.origwidth;t.style.width=e+"px";t.style.height=t.dataset.origheight*i+"px"})};t();window.onresize=function(){t()}})();